import React, { Component } from "react";
import './mainstatscard.css';

const MainStatsCard = ({cases1, cases2, cases3, ...props}) => (
      <div className="card card-stats">
        <div className="content" >
            <h5 className='stats__title' style={{ marginTop:5 }} >ACTIVE CASES</h5>
            <hr />
            <div className='activeCases'>{cases1}</div>
            <div className='subheading'>infected patients</div>

            <div>
                <div className='left'>{cases2}</div>
                <div className='right'>{cases3}</div>
            </div>
        </div>
      </div>
);

export default MainStatsCard;
