import React, { useEffect, useState } from "react";
import MyResponsivePie from "./PieChart";
import "./piechartcontainer.css";
import Card from "@material-ui/core/Card";
import { Row, Col } from "react-bootstrap";
import CardTrend from "../CardTrend/CardTrend";

const PieChartContainer = () => {
  const [continents, setcontinents] = useState([]);

  const continentColors = {
    "North America": "hsl(197, 37%, 24%)",
    "South America": "hsl(197, 37%, 24%)",
    Asia: "hsl(0, 0%, 0%)",
    Europe: "hsl(197, 37%, 24%)",
    Africa: "hsl(197, 37%, 24%)",
    "Australia/Oceania": "hsl(197, 37%, 24%)",
  };

  useEffect(() => {
    const getContinentData = () => {
      fetch("https://disease.sh/v3/covid-19/continents")
        .then((response) => response.json())
        .then((data) => {
          const continents = data.map((cont) => ({
            id: cont.continent,
            label: cont.continent,
            value: cont.cases,
            color: continentColors[cont["continent"]],
          }));

          setcontinents(continents);
        });
    };

    getContinentData();
  }, []);

  return (
    <div>
      <div className="dailyChart_header"></div>

      <Row>
        <Col md={6}>
          <CardTrend
            statsIcon="fa fa-history"
            id="chartHours"
            title="Case Distribution"
            category="Cases Per Day"
            stats="Updated 10 minutes ago"
            img_url="virus"
            content={
              <div className="ct-chart">
                <MyResponsivePie data={continents} className="app__graph" />
              </div>
            }
          />
        </Col>
        <Col md={6}>
          <CardTrend
            statsIcon="fa fa-history"
            id="chartHours"
            title="Case Distribution Per Continent"
            category="Deaths Per Day"
            stats="Updated 3 minutes ago"
            img_url="globe"
            content={
              <div className="ct-chart">
                <MyResponsivePie data={continents} className="app__graph" />
              </div>
            }
          />
        </Col>
      </Row>
    </div>
  );
};

export default PieChartContainer;
