import React, { useState, useEffect } from "react";
import MainStatsCard from '../MainStatsCard/MainStatsCard';
import { Grid, Row, Col } from "react-bootstrap";
import numeral from "numeral";

const HeaderStats = () => {
    const [countryInfo, setCountryInfo] = useState({});

    useEffect(() => {
        fetch("https://disease.sh/v3/covid-19/all")
          .then((response) => response.json())
          .then((data) => {
            setCountryInfo(data);
          });
    }, []);

    const mildCases =  parseInt(countryInfo.active)-parseInt(countryInfo.critical);
    const casesWithOutcome = parseInt(countryInfo.cases)-parseInt(countryInfo.recovered);
    const recoveredPercentage = Math.round((parseInt(countryInfo.recovered)/parseInt(countryInfo.cases))*100);

    const prettyPrintStat = (stat) =>
            stat ? `+${numeral(stat).format("0.0a")}` : "+0";

    return(
        <Row>
            <Col lg={3} sm={12}>
              <MainStatsCard
                title= 'TOTAL ACTIVE CASES'
                cases1 = {prettyPrintStat(countryInfo.active)}
                subheading1 = 'Total infected patients'
                cases2 = {numeral(countryInfo.critical).format("0.0a")}
                subheading2 = 'in Mild condition'
                cases3 = {numeral(mildCases).format("0.0a")}
                subheading3 = 'Serious/Critical'
                imageUrl='https://i.ibb.co/KF1YfN4/calendar.png'
                percentage={numeral(recoveredPercentage).format('0%')}
              />
            </Col>
            <Col lg={3} sm={12}>
            <MainStatsCard
                title= 'TOTAL CLOSED CASES'
                cases1 = {prettyPrintStat(casesWithOutcome)}
                subheading1 = 'Cases with an outcome'
                cases2 = {numeral(countryInfo.recovered).format("0.0a")}
                subheading2 = 'Recovered'
                cases3 = {numeral(countryInfo.deaths).format("0.0a")}
                subheading3 = 'Deaths'
                imageUrl='https://i.ibb.co/5KnPmQ4/error.png'
              />
            </Col>
            <Col lg={3} sm={12}>
            <MainStatsCard
                title= 'TODAY ACTIVE CASES'
                cases1 = {prettyPrintStat(countryInfo.todayCases)}
                subheading1 = 'Active cases'
                cases2 = {numeral(countryInfo.todayRecovered).format("0.0a")}
                subheading2 = 'Recovered cases'
                cases3 = {numeral(countryInfo.todayDeaths).format("0.0a")}
                subheading3 = 'Deaths'
                imageUrl='https://i.ibb.co/XCDZppm/shipping-and-delivery.png'
              />
            </Col>
            <Col lg={3} sm={12}>
            <MainStatsCard
                title= 'CASES PER 1 MILLION'
                cases1 = {prettyPrintStat(countryInfo.casesPerOneMillion)}
                subheading1 = 'Cases/1 million'
                cases2 = {numeral(countryInfo.recoveredPerOneMillion).format("0.0a")}
                subheading2 = 'Recovered/1 million'
                cases3 = {numeral(countryInfo.deathsPerOneMillion).format("0.0a")}
                subheading3 = 'Deaths/1 million'
                imageUrl='https://i.ibb.co/BqG0kcL/orders.png'
              />
            </Col>
          </Row>
    );


};

export default HeaderStats
