import React, { Component } from "react";
import './mainstatscard.css';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';

const MainStatsCard = ({cases1, cases2, cases3, title, subheading1, subheading2, subheading3, imageUrl, ...props}) => (

    <Card className='stats__card'>
        <CardContent>
            <img src={imageUrl} alt="icon"/>
            <h5 className='stats__title' style={{ marginTop:5 }} >{title}</h5>
                <hr />
            <div className='activeCases'>{cases1}</div>
            <div className='subheading'>{subheading1}</div>

            <div className='left'>
                <div>{cases2}</div>
                <div className='subheading'>{subheading2}</div>
            </div>
            
            <div className='right'>
                <div >{cases3}</div>
                <div className='subheading'>{subheading3}</div>
            </div>
            
            


        </CardContent>
    </Card>
    
  

);

export default MainStatsCard;
