import React, { Component } from "react";
import { Row, Col } from "react-bootstrap";

const MainStatsCard = ({cases1, cases2, cases3, ...props}) => (
      <div >
        <div style={{ padding:0 }} >
            <h3 style={{ fontSize:16, textAlign: "center", padding:6  }}>ACTIVE CASES</h3>
            <hr />
            <span>{cases1}</span><br />
            <span>{cases2}</span><br />
            <span>{cases3}</span>
        </div>
      </div>
);

export default MainStatsCard;
