import React, { Component } from "react";
import './mainstatscard.css';

const MainStatsCard = ({cases1, cases2, cases3, ...props}) => (
      <div className="card card-stats">
        <div className="content" style={{ padding:0 }} >
            <h5 className='stats__title'>ACTIVE CASES</h5>
            <hr />
            <div className='activeCases'>{cases1}</div>
            <span></span><br />
            <span>{cases2}</span><br />
            <span>{cases3}</span>
        </div>
      </div>
);

export default MainStatsCard;
